/**
 * @template T
 * @param {T} [self]
 * @returns {{
 *   root: import('../types/context').RootContext;
 *   parent: THREE.Object3D;
 *   self: T;
 * }}
 */
export function setup<T>(self?: T): {
    root: import('../types/context').RootContext;
    parent: THREE.Object3D;
    self: T;
};
/** @param {import('../types/context').RootContext} context */
export function set_root(context: import('../types/context').RootContext): import("../types/context").RootContext;
export function getInvalidator(): () => void;
export function getCapabilities(): import("three").WebGLCapabilities;
